import NavBar from './components/NavBar'
import Menu from './components/Menu'
import Cart from './components/Cart'
import FoodsCategory from './components/FoodsCategory'
import { useEffect } from 'react'
import {useDispatch,useSelector} from 'react-redux'
import {fetchFoodsList} from './store/modules/takeaway'
import './App.scss'

const foodsList = [
  {
    "tag": "318569657",
    "name": "一人套餐",
    "foods": [
      {
        "id": 8078956697,
        "name": "烤羊肉串(10串)",
        "like_ratio_desc": "好评度100%",
        "month_saled": 40,
        "unit": "10串",
        "food_tag_list": ["点评网友推荐"],
        "price": 90,
        "picture": "https://yjy-teach-oss.oss-cn-beijing.aliyuncs.com/meituan/1.jpg",
        "description": "",
        "tag": "318569657"
      },
      {
        "id": 7384994864,
        "name": "腊味煲仔饭",
        "like_ratio_desc": "好评度81%",
        "month_saled": 100,
        "unit": "1人份",
        "food_tag_list": [],
        "price": 39,
        "picture": "https://yjy-teach-oss.oss-cn-beijing.aliyuncs.com/meituan/2.png",
        "description": "",
        "tag": "318569657"
      },
      {
        "id": 2305772036,
        "name": "鸡腿胡萝卜焖饭",
        "like_ratio_desc": "好评度91%",
        "month_saled": 300,
        "unit": "1人份",
        "food_tag_list": [],
        "price": 34.32,
        "picture": "https://yjy-teach-oss.oss-cn-beijing.aliyuncs.com/meituan/3.jpg",
        "description": "主料：大米、鸡腿、菜心、胡萝卜",
        "tag": "318569657"
      },
      {
        "id": 2233861812,
        "name": "小份酸汤莜面鱼鱼+肉夹馍套餐",
        "like_ratio_desc": "好评度73%",
        "month_saled": 600,
        "unit": "1人份",
        "food_tag_list": ["“口味好,包装很好～点赞”"],
        "price": 34.32,
        "picture": "https://yjy-teach-oss.oss-cn-beijing.aliyuncs.com/meituan/4.jpeg",
        "description": "酸汤莜面鱼鱼，主料：酸汤、莜面 肉夹馍，主料：白皮饼、猪肉",
        "tag": "318569657"
      }
    ]
  }
]

const App = () => {
  //获取数据
  //触发action
  //1:useDispatch->dispatch
  //2:将创建action对象的actionCreater导入进来
  //3:在useEffect 中出发action
  //4:useSelector 获取数据
  //获取dispatch
  const dispatch = useDispatch();
  //获取store中的数据
  const {foodsList} = useSelector(state=>state.foods);
  console.log('foodsList',foodsList);
  useEffect(()=>{
    //触发action
    dispatch(fetchFoodsList());
  },[dispatch]);

  return (
    <div className="home">
      {/* 导航 */}
      <NavBar />

      {/* 内容 */}
      <div className="content-wrap">
        <div className="content">
          <Menu />

          <div className="list-content">
            <div className="goods-list">
              {/* 外卖商品列表 */}
              {foodsList.map((item,index) => {
                return (
                  activeIndex === index &&
                  <FoodsCategory
                    key={item.tag}
                    // 列表标题
                    name={item.name}
                    // 列表商品
                    foods={item.foods}
                  />
                )
              })}
            </div>
          </div>
        </div>
      </div>

      {/* 购物车 */}
      <Cart />
    </div>
  )
}

export default App
